import { HttpClient } from "@angular/common/http";
import { Injectable, ViewChild } from "@angular/core";
import { NgForm } from "@angular/forms";
import { Post } from "./post.model";
import {map} from'rxjs/operators';
import { Subject } from "rxjs";

@Injectable({providedIn:'root'})
export class PostsService{
    // @ViewChild('postForm', { static: false }) signupForm!: NgForm;
    error=new Subject<string>();

    constructor(private http:HttpClient){}
    createAndStorePost(title:string,content:string){
        const postData:Post={title:title,content:content}
        this.http.post<{name:string}>("https://ng-complete-guide-4db6b-default-rtdb.firebaseio.com/posts.json", postData).subscribe(responseData => {
            console.log(responseData);
        
      
          },error=>{
            this.error.next(this.error);
          })  
          
      

    }
    fetchPosts(){
        return this.http.get<{[key:string]:Post}>("https://ng-complete-guide-4db6b-default-rtdb.firebaseio.com/posts.json").pipe(map((responseData:{[key:string]:Post}) => {
      const postsArray:Post[] = [];
      for (const key in responseData) {
        if (responseData.hasOwnProperty(key)) {
          postsArray.push({...responseData[key], id: key });
        }
      }
      return postsArray;

    }))
    }
    deletePosts(){
       return this.http.delete("https://ng-complete-guide-4db6b-default-rtdb.firebaseio.com/posts.json")
    }
}
