import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { map } from 'rxjs/operators';
import { Posts } from './post.model';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'app';
  loadedPosts = [];



  ngOnInit() {
    this.fetchPosts();
  }
  constructor(private http: HttpClient) { }
  onCreatePost(postData: Posts) {
    // console.log(postData);
    this.http.post("https://ng-complete-guide-4db6b-default-rtdb.firebaseio.com/posts.json", postData).subscribe(responseData => {
      console.log(responseData)
    })

  }
  onFetchPosts() {
    this.fetchPosts();

  }
  private fetchPosts() {
    this.http.get("https://ng-complete-guide-4db6b-default-rtdb.firebaseio.com/posts.json").pipe(map((responseData:{[key:string]:Posts}) => {
      const postsArray:pOS = [];
      for (const key in responseData) {
        if (responseData.hasOwnProperty(key)) {
          postsArray.push({...responseData[key], id: key });
        }
      }
      return postsArray;

    })).subscribe(posts => {
      console.log(posts)
    })
  }
}
